	protected function doSave(PropelPDO $con)
	{
		$affectedRows = 0; // initialize var to track total num of affected rows
		if (!$this->alreadyInSave) {
			$this->alreadyInSave = true;

			// We call the save method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aResultado !== null) {
				if ($this->aResultado->isModified() || $this->aResultado->isNew()) {
					$affectedRows += $this->aResultado->save($con);
				}
				$this->setResultado($this->aResultado);
			}

			if ($this->aCampoResultadoPatogeno !== null) {
				if ($this->aCampoResultadoPatogeno->isModified() || $this->aCampoResultadoPatogeno->isNew()) {
					$affectedRows += $this->aCampoResultadoPatogeno->save($con);
				}
				$this->setCampoResultadoPatogeno($this->aCampoResultadoPatogeno);
			}

			if ($this->aPruebaBioquimica !== null) {
				if ($this->aPruebaBioquimica->isModified() || $this->aPruebaBioquimica->isNew()) {
					$affectedRows += $this->aPruebaBioquimica->save($con);
				}
				$this->setPruebaBioquimica($this->aPruebaBioquimica);
			}

			if ($this->aPositivoNegativo !== null) {
				if ($this->aPositivoNegativo->isModified() || $this->aPositivoNegativo->isNew()) {
					$affectedRows += $this->aPositivoNegativo->save($con);
				}
				$this->setPositivoNegativo($this->aPositivoNegativo);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = PatogenosPeer::ID;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
				if ($this->isNew()) {
					$pk = PatogenosPeer::doInsert($this, $con);
					$affectedRows += 1; // we are assuming that there is only 1 row per doInsert() which
										 // should always be true here (even though technically
										 // BasePeer::doInsert() can insert multiple rows).

					$this->setId($pk);  //[IMV] update autoincrement primary key

					$this->setNew(false);
				} else {
					$affectedRows += PatogenosPeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
			}

			$this->alreadyInSave = false;

		}
		return $affectedRows;
	} // doSave()

