	 * an aggreagated array of ValidationFailed objects will be returned.
	 *
	 * @param      array $columns Array of column names to validate.
	 * @return     mixed <code>true</code> if all validations pass; array of <code>ValidationFailed</code> objets otherwise.
	 */
	protected function doValidate($columns = null)
	{
		if (!$this->alreadyInValidation) {
			$this->alreadyInValidation = true;
			$retval = null;

			$failureMap = array();


			// We call the validate method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aResultado !== null) {
				if (!$this->aResultado->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aResultado->getValidationFailures());
				}
			}

			if ($this->aCampoResultadoPatogeno !== null) {
				if (!$this->aCampoResultadoPatogeno->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aCampoResultadoPatogeno->getValidationFailures());
				}
			}

			if ($this->aPruebaBioquimica !== null) {
				if (!$this->aPruebaBioquimica->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aPruebaBioquimica->getValidationFailures());
				}
			}

			if ($this->aPositivoNegativo !== null) {
				if (!$this->aPositivoNegativo->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aPositivoNegativo->getValidationFailures());
				}
			}


			if (($retval = PatogenosPeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}



			$this->alreadyInValidation = false;
		}

		return (!empty($failureMap) ? $failureMap : true);
	}

