		if ($this->isColumnModified(PatogenosPeer::ID)) $criteria->add(PatogenosPeer::ID, $this->id);
		if ($this->isColumnModified(PatogenosPeer::NOMBRE_PRUEBA)) $criteria->add(PatogenosPeer::NOMBRE_PRUEBA, $this->nombre_prueba);
		if ($this->isColumnModified(PatogenosPeer::CREATED_AT)) $criteria->add(PatogenosPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(PatogenosPeer::UPDATED_AT)) $criteria->add(PatogenosPeer::UPDATED_AT, $this->updated_at);
		if ($this->isColumnModified(PatogenosPeer::RESULTADO_ID)) $criteria->add(PatogenosPeer::RESULTADO_ID, $this->resultado_id);
		if ($this->isColumnModified(PatogenosPeer::CAMPO_RESULTADO_PATOGENO_ID)) $criteria->add(PatogenosPeer::CAMPO_RESULTADO_PATOGENO_ID, $this->campo_resultado_patogeno_id);
		if ($this->isColumnModified(PatogenosPeer::PRUEBA_BIOQUIMICA_ID)) $criteria->add(PatogenosPeer::PRUEBA_BIOQUIMICA_ID, $this->prueba_bioquimica_id);
		if ($this->isColumnModified(PatogenosPeer::POSITIVO_NEGATIVO_ID)) $criteria->add(PatogenosPeer::POSITIVO_NEGATIVO_ID, $this->positivo_negativo_id);

