					$affectedRows += $this->aMuestra->save($con);
				}
				$this->setMuestra($this->aMuestra);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = ResultadoPeer::ID;
			}

			// If this object has been modified, then save it to the database.
