			$this->aTiempoIncubacion = null;
			$this->aMuestra = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseResultado:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseResultado::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BaseResultado
